sp_widget.client_script:
api.controller = function ($window, $scope, $http) {

  /* widget controller */
  var c = this;

  $scope.place = "";
  $scope.address = "";
  $scope.city = "";
  $scope.zipcode = "";
  $scope.state = "";

  $scope.latitude = "";
  $scope.longitude = "";

  //$scope.buni = "";

//  $scope.waiting = false;

  $scope.addressSuggestions = [];
  $scope.searching = true;

	/*
  $scope.handleKeyPress = function(event) {
    if (event.which === 13) {
      alert("entered");
      $scope.searchingActive();
      // 13 is the key code for Enter key
      // Call your function here
    }
  };

	*/


  $scope.searchingActive = function(){
		if($scope.searching == true){
			$scope.searching = false
		}
	 };

  $scope.getResults = function (place) {

    if(place.length == 0){
      return;
    }

    var ga = new GlideAjax('BingMapClientCallable');
    ga.addParam('sysparm_name', 'fetchAddressDetail');
    ga.addParam('sysparm_place', place);
    var parsedObj = [];

    ga.getXML(function (response) {

      var res = response.responseXML.documentElement.getAttribute("answer");
      parsedObj = JSON.parse(res);
		alert(JSON.stringify(parsedObj));


      $scope.addressSuggestions = parsedObj;

      if($scope.addressSuggestions.length > 1){
        $scope.searching = true;
      } 
      if($scope.addressSuggestions.length == 0 && $scope.searching == false){
        $scope.searching = false;
      }

      return;	
    });
  };

  $scope.getCurrent = function (opt) {
    $scope.searching = false;

    if(opt.__type == "Address"){
      $scope.address = opt.address.formattedAddress;
      $scope.city = opt.address.locality;
      $scope.zipcode = opt.address.postalCode;
      $scope.state = opt.address.adminDistrict;
      $scope.place = "";

      $scope.latitude = "";
      $scope.longitude = "";
    }

    if(opt.__type == "LocalBusiness"){

      $scope.waiting = true;

      $scope.address = "";
      $scope.city = "";
      $scope.zipcode = "";
      $scope.state = "";
      $scope.place = "";

      $scope.latitude = "";
      $scope.longitude = "";

      var ga = new GlideAjax('BingMapClientCallable');
      ga.addParam('sysparm_name', 'fetchGeagraphyDetail');
      ga.addParam('sysparm_addressLine', opt.address.formattedAddress);

      var parsedObj = [];
      ga.getXML(function (response) {
        var res = response.responseXML.documentElement.getAttribute("answer");
        parsedObj = JSON.parse(res);

        $scope.address = opt.address.formattedAddress;
        $scope.city = opt.address.locality;
        $scope.zipcode = opt.address.postalCode;
        $scope.state = opt.address.adminDistrict;

        $scope.latitude = parsedObj[0];
        $scope.longitude = parsedObj[1];

        $scope.$apply();
      });
    }
  };

};

